import React, { useState } from 'react';
import Modal from './pages/productModal';

export default ({ product }) => {
  const { id, title, description, category, image, price } = product;
  const [isModal, setIsModal] = useState(false);

  return (
    <div className='card border-dark mb-3' style={{ width: '30%', margin: '10px' }}>
      <img src={image} className='card-img-top' alt='...' />
      <div className='card-body'>
        <h5 className='card-title'>{title}</h5>
        <p>{category}</p>
        <p className='card-text'>{description}</p>
        <h5 className='card-title'>â‚¬{price}</h5>
        <button href={id} className='btn btn-primary' onClick={() => { setIsModal(true); }}>Watch this product</button>
      </div>
      <Modal show={isModal} onHide={() => setIsModal(true)}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <button variant='secondary' onClick={() => setIsModal(false)}>
      Close
          </button>
        </Modal.Footer>
      </Modal>
  );
    </div>
  );
};
