import React, { useState } from 'react';
import Modal from './pages/productModal';

export default ({ product }) => {
  const { id, title, description, category, image, price } = product;
  const [modalShow, setModalShow] = useState(false);

  return (
    <div className='card border-dark mb-3' style={{ width: '30%', margin: '10px' }}>
      <img src={image} className='card-img-top' alt='...' />
      <div className='card-body'>
        <h5 className='card-title'>{title}</h5>
        <p>{category}</p>
        <p className='card-text'>{description}</p>
        <h5 className='card-title'>â‚¬{price}</h5>
        <button href={id} className='btn btn-primary' onClick={setModalShow(true)}>Watch this product</button>
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </div>
    </div>
  );
};
